$(function(){module("bootstrap-typeahead");test("should be defined on jquery object",function(){ok($(document.body).typeahead,"alert method is defined")});test("should return element",function(){ok($(document.body).typeahead()[0]==document.body,"document.body returned")});test("should listen to an input",function(){var a=$("<input />");a.typeahead();ok(a.data("events").blur,"has a blur event");ok(a.data("events").keypress,"has a keypress event");ok(a.data("events").keyup,"has a keyup event");if($.browser.webkit||$.browser.msie){ok(a.data("events").keydown,"has a keydown event")}else{ok(a.data("events").keydown,"does not have a keydown event")}});test("should create a menu",function(){var a=$("<input />");ok(a.typeahead().data("typeahead").$menu,"has a menu")});test("should listen to the menu",function(){var b=$("<input />"),a=b.typeahead().data("typeahead").$menu;ok(a.data("events").mouseover,"has a mouseover(pseudo: mouseenter)");ok(a.data("events").click,"has a click")});test("should show menu when query entered",function(){var b=$("<input />").typeahead({source:["aa","ab","ac"]}),a=b.data("typeahead");b.val("a");a.lookup();ok(a.$menu.is(":visible"),"typeahead is visible");equals(a.$menu.find("li").length,3,"has 3 items in menu");equals(a.$menu.find(".active").length,1,"one item is active");a.$menu.remove()});test("should accept data source via synchronous function",function(){var b=$("<input />").typeahead({source:function(){return["aa","ab","ac"]}}),a=b.data("typeahead");b.val("a");a.lookup();ok(a.$menu.is(":visible"),"typeahead is visible");equals(a.$menu.find("li").length,3,"has 3 items in menu");equals(a.$menu.find(".active").length,1,"one item is active");a.$menu.remove()});test("should accept data source via asynchronous function",function(){var b=$("<input />").typeahead({source:function(c,d){d(["aa","ab","ac"])}}),a=b.data("typeahead");b.val("a");a.lookup();ok(a.$menu.is(":visible"),"typeahead is visible");equals(a.$menu.find("li").length,3,"has 3 items in menu");equals(a.$menu.find(".active").length,1,"one item is active");a.$menu.remove()});test("should not explode when regex chars are entered",function(){var b=$("<input />").typeahead({source:["aa","ab","ac","mdo*","fat+"]}),a=b.data("typeahead");b.val("+");a.lookup();ok(a.$menu.is(":visible"),"typeahead is visible");equals(a.$menu.find("li").length,1,"has 1 item in menu");equals(a.$menu.find(".active").length,1,"one item is active");a.$menu.remove()});test("should hide menu when query entered",function(){stop();var b=$("<input />").typeahead({source:["aa","ab","ac"]}),a=b.data("typeahead");b.val("a");a.lookup();ok(a.$menu.is(":visible"),"typeahead is visible");equals(a.$menu.find("li").length,3,"has 3 items in menu");equals(a.$menu.find(".active").length,1,"one item is active");b.blur();setTimeout(function(){ok(!a.$menu.is(":visible"),"typeahead is no longer visible");start()},200);a.$menu.remove()});test("should set next item when down arrow is pressed",function(){var b=$("<input />").typeahead({source:["aa","ab","ac"]}),a=b.data("typeahead");b.val("a");a.lookup();ok(a.$menu.is(":visible"),"typeahead is visible");equals(a.$menu.find("li").length,3,"has 3 items in menu");equals(a.$menu.find(".active").length,1,"one item is active");ok(a.$menu.find("li").first().hasClass("active"),"first item is active");b.trigger({type:"keydown",keyCode:40});ok(a.$menu.find("li").first().next().hasClass("active"),"second item is active");b.trigger({type:"keydown",keyCode:38});ok(a.$menu.find("li").first().hasClass("active"),"first item is active");a.$menu.remove()});test("should set input value to selected item",function(){var c=$("<input />").typeahead({source:["aa","ab","ac"]}),a=c.data("typeahead"),b=false;c.val("a");a.lookup();c.change(function(){b=true});$(a.$menu.find("li")[2]).mouseover().click();equals(c.val(),"ac","input value was correctly set");ok(!a.$menu.is(":visible"),"the menu was hidden");ok(b,"a change event was fired");a.$menu.remove()});test("should start querying when minLength is met",function(){var b=$("<input />").typeahead({source:["aaaa","aaab","aaac"],minLength:3}),a=b.data("typeahead");b.val("aa");a.lookup();equals(a.$menu.find("li").length,0,"has 0 items in menu");b.val("aaa");a.lookup();equals(a.$menu.find("li").length,3,"has 3 items in menu");a.$menu.remove()})});